class s{constructor(t){this.collectionName=t}sortCollection(t=[]){return t.sort((o,c)=>new Date(c.created_at).getTime()-new Date(o.created_at).getTime())}addItem(t){const e=this.getCollection();e.push(t),localStorage.setItem(this.collectionName,JSON.stringify(e))}getItem(t){return this.getCollection().find(o=>o.id===t)}updateItem(t,e){const c=this.getCollection().map(n=>n.id===t?{...n,...e}:n);localStorage.setItem(this.collectionName,JSON.stringify(c))}removeItem(t){const o=this.getCollection().filter(c=>c.id!==t);localStorage.setItem(this.collectionName,JSON.stringify(o))}clearCollection(){localStorage.removeItem(this.collectionName)}getCollection(){const t=localStorage.getItem(this.collectionName);return t?this.sortCollection(JSON.parse(t)):[]}}const m=i=>{const t=new s(i);return{addItem:l=>{t.addItem(l)},getItem:l=>t.getItem(l),updateItem:(l,r)=>t.updateItem(l,r),removeItem:l=>t.removeItem(l),getCollection:()=>t.getCollection()}};export{m as u};
